mapper.update:
UPDATE EMPLOYEE
	SET FAIL = CASE WHEN PASSWORD = #{password} THEN 0 ELSE FAIL + 1 END
	WHERE ID = #{id}

mapper.select:
	SELECT ID, PASSWORD, EMAIL
        FROM EMPLOYEE e
        WHERE ID=#{id} AND EMAIL = #{email}

mapper.update:
	UPDATE EMPLOYEE 
        SET EXIT_DAY = #{exit_day}
        WHERE ID = #{id} AND ESTATUS = 'N'
