mapper.select:
SELECT n.NOTI_ID, GUBUN, NTYPE, SENDER, ALERT_TIME, CONTENT, RECEIVER_ID, "READ", c.CODE_NAME
	FROM NOTIFICATION n JOIN RECEIVER r
	ON n.NOTI_ID = r.NOTI_ID JOIN COMMON c 
	ON n.NTYPE = c.CODE AND c."TYPE" = '알림'
	WHERE r.RECEIVER_ID = #{id}
	AND c.CODE_NAME = #{code_name}

mapper.update:
UPDATE RECEIVER 
	SET "READ" = 'Y'
	WHERE RECEIVER_ID = #{id}
	AND NOTI_ID = #{noti_id}
