mapper.update:
UPDATE RESERVATION SET
PHONE=#{phone}, MEETINGTITLE=#{meetingtitle}
WHERE
RESERVENO=#{reserveno}

mapper.delete:
    	BEGIN
    		SAVEPOINT START_TRANSACTION;
    		DELETE FROM CHECKLIST
    			WHERE RESERVENO = #{reserveno};
    		DELETE FROM RESERVATION
    			WHERE RESERVENO = #{reserveno};
    	COMMIT
		EXCEPTION
    		WHEN OTHERS THEN
       			ROLLBACK TO START_TRANSACTION;
        		RAISE
		END
